export type Paths = {
    root: string;
    db: string;
    middlewares: string;
    injectors: string;
    rewriters: string;
    store: string;
    static: string;
    snapshots: string;
};
export type Configs = {
    port: number;
    host: string;
    base: string;
    id: string;
    dbMode: 'mock' | 'fetch' | 'multi' | 'config';
    reverse: boolean;
    readOnly: boolean;
    noCors: boolean;
    noGzip: boolean;
    noCache: boolean;
    logger: boolean;
    bodyParser: boolean;
    cookieParser: boolean;
    watch: boolean;
    quiet: boolean;
    log: boolean;
    homePage: boolean;
};
export type CliOptions = Paths & Configs & {
    config: string;
    init: boolean;
    _: string[];
};
declare const _default: (pkg: any) => Promise<CliOptions>;
export default _default;
